{
    "description": "Notify legacy QuantumLeap of ALL Streetlight changes (NGSI-v2 format)",
    "subject": {
        "entities": [
            {
                "idPattern": ".*",
                "type": "Streetlight"
            }
        ]
    },
    "notification": {
        "http": {
            "url": "http://quantumleap:8668/v2/notify"
        },
        "attrsFormat": "normalized"
    }
}
